<link rel="stylesheet" href="<?php echo ASSETS ?>/css/search.css" type="text/css"> </head>  <body> <!--Cabecera--> <header class="header container-fluid"> <nav class="header__nav row"> <div class="header__nav__div"> <div class="header__nav__div__div navbar"> <form method="GET" class="header__nav__div__div__form d-flex"> <div class="header__nav__div__div__form__div input-group"> <input name="search" type="text" class="header__nav__div__div__form__div__input form-control" placeholder="search"> <button type="submit" class="header__nav__div__div__form__div__btn input-group-text"><i class="bi bi-search"></i></button> </div> </form> </div> </div> </nav> </header>  <!--Seccion--> <section class="search container-fluid"> <div class="search__div row"> <?php /** * si el estado de la respuesta no es verdadero significa que no se * encontro ninguna coincidencia y por lo tanto imprimira un mensaje de error * que nos envia el controlador * * @var array $response['state'] contiene el estado de la respuesta */ if($response['state'] != true){ echo "<div class='container text-center alert alert-danger' role='alert'><i class='bi bi-exclamation-triangle'></i> ".$response['data']."</div>"; }else{ /** * si el estado de la respuesta es verdadero significa que se nos ha retornado * un array con las coincidencias hayadas por lo cual se recorrera el array y se * empezara a imprimir * @var array $response['data'] contiene las coincidencias hayadas */ foreach($response['data'] as $row){ echo "<a class='search__div__div__a' href='".$row['site_url']."' target='_blank' title='".$row['site_title']."'> <div class='search__div__div col-auto'> <h4 class='search__div__div__a__h4'>".$row['site_title']." <i class='bi bi-globe'></i></h4> <small class='search__div__div__a__small'>".$row['site_url']."</small> <p class='search__div__div__a__p'>".$row['site_description']."</p> </div> </a>"; } } unset($response);  ?> </div> </section>